#include <unistd.h>
#define MAX 80
#define PORT 8080
#define SA struct sockaddr

void reference(int connfd)
{
	char buffer[1024] = {0};
	int valread;
	char **referenceValue;
	char singleString[999999];
	char line[128];
	int num = 90663;
	char *ret;//[] = {};


	referenceValue = (char **)malloc(num * sizeof(char *));
	for (int m = 0; m < num; m++)
		referenceValue[m] = (char *)malloc(sizeof(line) * sizeof(char));

	for (int x = 0; x < num; x++)
	{
		valread = read(connfd, buffer, 127);
		strcpy(referenceValue[x], &buffer);
		// printf("%s\n\n\n", &buffer);
	}

	//GET VALUES FROM MEMORY
	for (int x = 0; x < num; x++)
	{
		char *val = "";
		int size = sizeof(val);
   		ret = strstr(referenceValue[x], val);
		if (ret != NULL){
			printf("VALLLL%s", val);
		}
		// printf("referenceValue: %s\n\n\n", referenceValue[x]);
	}
	// free(referenceValue);

	char *hello = "Hello from server";
	send(connfd, hello, strlen(hello), 0);
}

int main()
{
	int sockfd, connfd, len;
	struct sockaddr_in servaddr, cli;

	sockfd = socket(AF_INET, SOCK_STREAM, 0);
	if (sockfd == -1)
	{
		printf("socket creation failed...\n");
		exit(0);
	}
	else
		printf("Socket successfully created..\n");
	bzero(&servaddr, sizeof(servaddr));

	servaddr.sin_family = AF_INET;
	servaddr.sin_addr.s_addr = htonl(INADDR_ANY);
	servaddr.sin_port = htons(PORT);

	if ((bind(sockfd, (SA *)&servaddr, sizeof(servaddr))) != 0)
	{
		printf("socket bind failed...\n");
		exit(0);
	}
	else
		printf("Socket successfully binded..\n");

	if ((listen(sockfd, 5)) != 0)
	{
		printf("Listen failed...\n");
		exit(0);
	}
	else
		printf("Server listening..\n");
	len = sizeof(cli);

	connfd = accept(sockfd, (SA *)&cli, &len);
	if (connfd < 0)
	{
		printf("server accept failed...\n");
		exit(0);
	}
	else
		printf("server accept the client...\n");

	reference(connfd);

	close(sockfd);
}
